module tb_top_module;

reg clk, resetn, x, y;
wire f, g;

top_module dut (
    .clk(clk),
    .resetn(resetn),
    .x(x),
    .y(y),
    .f(f),
    .g(g)
);

initial begin
    // Clock definition
    clk = 0;
    forever #5 clk = ~clk; // Clock with a period of 10 ns
end

initial begin
    // Test case initialization
    resetn = 0; x = 0; y = 0;

    #10;
    resetn = 1; // Release reset
    #10; // Wait one clock cycle

    // f should be 1 for only one clock cycle after reset
    if (f !== 1) $display("Test case failed: f should be 1 after reset");

    #10; // Wait for next clock cycle
    if (f !== 0) $display("Test case failed: f should be 0 after first cycle post-reset");

    // Provide input x = 1, 0, 1 for three successive clocks
    x = 1;
    #10;
    x = 0;
    #10;
    x = 1;
    #10; // Wait one more cycle to see if g is set

    if (g !== 1) $display("Test case failed: g should be 1 after x = 1,0,1 sequence");

    // Test y = 0 for two cycles, g should become 0 permanently
    y = 0;
    #10;
    y = 0;
    #20; // Check after two cycles
    if (g !== 0) $display("Test case failed: g should be 0 permanently after two cycles of y=0");

    // Reset system and test g stays at 1 when y turns 1 within two cycles
    resetn = 0;
    #10;
    resetn = 1; x = 1; y = 0;
    #10; x = 0;
    #10; x = 1;
    #10; y = 1; // y is 1 within 2 cycles after g is supposed to be set

    if (g !== 1) $display("Test case failed: g should be set to 1 when y is 1 within two cycles");

    #50; // Wait longer to check g remains 1
    if (g !== 1) $display("Test case failed: g should remain 1 permanently once y has been 1 within two cycles after g being 1");

    // Check for no test case failures in simulation messages
    $display("all test cases passed");

    // Finish the simulation
    $finish;
end

