    initial begin
        clk = 0;
        forever #5 clk = ~clk; // Clock period of 10ns
    end

    // Test cases
    initial begin
        // Initialize inputs
        areset = 0; bump_left = 0; bump_right = 0; ground = 1;

        // Assert reset
        @(posedge clk) areset = 1;
        @(posedge clk) areset = 0;
        @(posedge clk);  // Additional delay to ensure the system is stable after reset

        // Check initial state (walking left)
        @(posedge clk);
        if (~(walk_left == 1'b1 && walk_right == 1'b0 && aaah == 1'b0)) begin
            $display("Initial state check failed.");
            $finish;
        end

        // Test 1: Bump left when walking left
        @(posedge clk) bump_left = 1; 
        @(posedge clk); 
        if (~(walk_left == 1'b0 && walk_right == 1'b1 && aaah == 1'b0)) begin
            $display("Test 1 failed.");
            $finish;
        end
        bump_left = 0;

        // Test 2: Ground disappears (should fall)
        @(posedge clk) ground = 0;
        @(posedge clk);
        if (~(walk_left == 1'b0 && walk_right == 1'b0 && aaah == 1'b1)) begin
            $display("Test 2 failed.");
            $finish;
        end

        // Test 3: Ground reappears
        @(posedge clk) ground = 1;
        @(posedge clk);
        if (~(walk_left == 1'b0 && walk_right == 1'b1 && aaah == 1'b0)) begin
            $display("Test 3 failed.");
            $finish;
        end

        // Test 4: Bump right when walking right
        @(posedge clk) bump_right = 1; 
        @(posedge clk);
        if (~(walk_left == 1'b1 && walk_right == 1'b0 && aaah == 1'b0)) begin
            $display("Test 4 failed.");
            $finish;
        end
        bump_right = 0;

        // Test 5: Dual bump
        @(posedge clk) {bump_left, bump_right} = 2'b11;
        @(posedge clk);
        if (~(walk_left == 1'b0 && walk_right == 1'b1 && aaah == 1'b0)) begin
            $display("Test 5 failed.");
            $finish;
        end
        {bump_left, bump_right} = 2'b00;

        // All test cases passed
        $display("All test cases passed");
        $finish;
    end

