    clk = 0;
    forever #5 clk = ~clk; // 100MHz Clock generation
  end

  initial begin
    $monitor("At time %t, data=%b, shift_ena=%b, counting=%b, done_counting=%b, done=%b, ack=%b", 
             $time, data, shift_ena, counting, done_counting, done, ack);
  end

  initial begin
    reset = 1; ack = 0; data = 0; done_counting = 0;
    #20;
    reset = 0; // Release reset
    // Sequence to detect 1101
    #10 data = 1;
    #10 data = 1;
    #10 data = 0;
    #10 data = 1; // Pattern 1101 detected

