    // After four bits, counting should start
    #10 if (counting !== 1) $display("Test case failed: counting not asserted after shift_ena.");
    done_counting = 1;  // done_counting goes high

    // Should notify done
    #10 if (done !== 1) $display("Test case failed: done not asserted after done_counting high.");
    ack = 1; // Acknowledge done
    #10 if (done !== 0) $display("Test case failed: done not cleared after ack.");

    #10 ack = 0; // Reset ack
    #100; // Wait for some time
    // Detect 1101 again as reset
    #10 data = 1;
    #10 data = 1;
    #10 data = 0;
    #10 data = 1;
    #10 if (shift_ena !== 1) $display("Test case failed: shift_ena not asserted on next sequence detection.");

