        clk = 0;
        forever #5 clk = ~clk;  // Clock period of 10 ns
    end

    // Test cases
    reg[31:0] test_vectors[5:0];  // Increase size as per the number of test cases
    integer i, j, error_cnt;
    reg[31:0] output_collected;
    reg fail_flag;

    initial begin
        // Initialize variables
        areset = 0;
        x = 0;
        error_cnt = 0;
        fail_flag = 0;

        // Test vector: 3, 2's complement: -3 (in binary: 1101)
        test_vectors[0] = 32'b1011_0000_0000_0000_0000_0000_0000_0000;  // 0b...0001101, LSB first
        test_vectors[1] = 32'b01_0000_0000_0000_0000_0000_0000_0000;     // ~0 (represented as 01 in binary). Use to stop test case.

        // Apply asynchronous reset
        @(negedge clk);
        areset = 1;
        repeat (2) @(posedge clk);
        areset = 0;

        // Apply test vectors
        for (i = 0; i < 2; i = i + 1) begin
            @(negedge clk);
            output_collected = 0;
            for (j = 31; j >= 0; j = j - 1) begin
                if (test_vectors[i][j] === 1'bx) break; // End of test case
                x = test_vectors[i][j];
                @(posedge clk);
                // Store result as MSB first
                output_collected = (output_collected << 1) | z;
            end

            // Check the results (expecting 1101 for test_vectors[0])
            if (i == 0 && output_collected !== 32'b1101) begin
                fail_flag = 1;  // Indicate test failure
                error_cnt = error_cnt + 1;
            end
        end

        // Check all test cases
        if (fail_flag === 0) begin
            $display("all test cases passed");
        end

