module tb_top_module;

  // Inputs
  reg clk;
  reg in;
  reg reset;

  // Outputs
  wire [7:0] out_byte;
  wire done;

  // Instantiate the Device Under Test (DUT)
  top_module dut (
      .clk(clk),
      .in(in),
      .reset(reset),
      .out_byte(out_byte),
      .done(done)
  );

  // Clock generation
  initial begin
    clk = 0;
    forever #5 clk = ~clk;  // Clock period 10ns
  end

  // Test Procedure
  initial begin
    // Initial conditions
    reset = 1;
    in = 1;
    #20;
    reset = 0;

    // Case 1: Sending valid byte 0x5A (01011010 in reverse order)
    send_byte(1'b0, 1'b0, 1'b1, 1'b1, 1'b0, 1'b1, 1'b0, 1'b1);
    check_byte(8'h5A, 1'b1);

    // Case 2: Faulty stop bit
    send_byte(1'b0, 1'b1, 1'b1, 1'b0, 1'b1, 1'b0, 1'b1, 1'b0);  // Intentionally wrong stop bit sequence
    check_byte(8'hZZ, 1'b0);  // Assuming Z means 'do not care'

    // Case 3: Another valid byte 0xA5 after correct handling of faulty stop bit
    send_byte(1'b0, 1'b1, 1'b0, 1'b0, 1'b1, 1'b0, 1'b1, 1'b0);
    check_byte(8'ha5, 1'b1);

    // Finish test and check results
    #10;
    if (tb_fail == 0)
      $display("all test cases passed");
    $finish;
  end

  reg [3:0] test_cases = 4'b0000;
  reg tb_fail = 0; // indicator for any failure, if at least one bit is 0, fail.

  task send_byte;
    input bit0, bit1, bit2, bit3, bit4, bit5, bit6, bit7;
    begin
      @(posedge clk) in = 1'b0;  // Start bit
      @(posedge clk) in = bit0;
      @(posedge clk) in = bit1;
      @(posedge clk) in = bit2;
      @(posedge clk) in = bit3;
      @(posedge clk) in = bit4;
      @(posedge clk) in = bit5;
      @(posedge clk) in = bit6;
      @(posedge clk) in = bit7;
      @(posedge clk) in = 1'b1;  // Stop bit
      @(posedge clk) in = 1'b1;  // Return to idle
    end
  endtask

  task check_byte;
    input [7:0] expected_byte;
    input expected_done;
    begin
      @(posedge clk);
      if (done !== expected_done || (done && out_byte !== expected_byte)) begin
        tb_fail = 1; // set test case fail flag
      end
    end
  endtask

endmodule

