module testbench;
    // Inputs to the DUT
    reg d;
    reg ena;

    // Output from the DUT
    wire q;

    // Instantiate the device under test
    top_module DUT (
        .d(d),
        .ena(ena),
        .q(q)
    );

    // Local variables
    integer test_count = 0;
    integer fail_count = 0;

    // Procedure to display test results
    task display_results;
        begin
            if (fail_count == 0) begin
                $display("all test cases passed");
            end
        end
    endtask

    // Procedure to apply test cases
    task apply_test;
        input reg test_d;
        input reg test_ena;
        input reg expected_q;
        reg actual_q;
        begin
            d = test_d;
            ena = test_ena;
            #10; // Wait for the DUT to process input
            actual_q = q;

            test_count = test_count + 1;
            if (actual_q !== expected_q) begin
                fail_count = fail_count + 1;
            end
        end
    endtask

    // Initial block to run the test vectors
    initial begin
        // Initialize inputs
        d = 0;
        ena = 0;
        #100; // Time for DUT to stabilize

        // Test vector 1: Check if latch captures the input while enable is high
        apply_test(1'b1, 1'b1, 1'b1);

        // Test vector 2: Check if the latch holds the value when enable goes low
        apply_test(1'b1, 1'b0, 1'b1);

        // Test vector 3: Change input when enable is low (output should not change)
        apply_test(1'b0, 1'b0, 1'b1);

        // Test vector 4: Enable high should update output to new input
        apply_test(1'b0, 1'b1, 1'b0);

        // Finalize test results
        #10; // Ensure last changes are captured
        display_results;

        // Finish simulation
        $finish;
    end
endmodule

