module testbench;

    reg clk, reset, data, ack;
    wire [3:0] count;
    reg counting, done;
    integer i;

    // Instantiate the Device Under Test (DUT)
    top_module dut(
        .clk(clk),
        .reset(reset),
        .data(data),
        .count(count),
        .counting(counting),
        .done(done),
        .ack(ack)
    );

    // Clock generation
    initial begin
        clk = 0;
        forever #5 clk = ~clk; // Generate a clock with 10ns period
    end

    // Stimulus here
    initial begin
        reset = 1; #10;  // Apply reset
        reset = 0; #10;  // Release reset

        // Test case 1: Simple 0000 delay scenario
        send_1101();
        send_delay(4'b0000);
        check_countdown(4'b0000);

        // ack handling
        ack = 1'b0; @(posedge done);
        ack = 1'b1; #10;

        // Test case 2: Mid-range 0101 delay scenario
        send_1101();
        send_delay(4'b0101);
        check_countdown(4'b0101);

        // ack handling
        ack = 1'b0; @(posedge done);
        ack = 1'b1; #10;

        // Test case 3: Maximum delay
        send_1101();
        send_delay(4'b1111);
        check_countdown(4'b1111);

        // ack handling
        ack = 1'b0; @(posedge done);
        ack = 1'b1; #10;

        // Test case completion check
        $display("all test cases passed");

        $finish;
    end

    // Function to send the 1101 start pattern
    task send_1101;
        begin
            @(posedge clk) data <= 1;  //1
            @(posedge clk) data <= 1;  //1
            @(posedge clk) data <= 0;  //0
            @(posedge clk) data <= 1;  //1
        end
    endtask

    // Function to send the delay pattern
    task send_delay(input [3:0] delay_val);
        begin
            for (i = 3; i >= 0; i = i - 1) begin
                @(posedge clk) data <= delay_val[i];
            end
        end
    endtask

    // Function to check countdown behavior
    task check_countdown(input [3:0] delay_val);
        reg [3:0] expected_count;
        integer j;
        for (j = 0; j <= delay_val; j = j + 1) begin
            expected_count = delay_val - j;
            #1000;  // Wait for the expected number of cycles
            if (count !== expected_count) begin
                $display("Test failed at delay value %d, expected %d, got %d", delay_val, expected_count, count);
                $finish;
            end
        end
    endtask

endmodule

