module testbench;
    reg clk;
    reg areset;
    reg bump_left;
    reg bump_right;
    reg ground;
    reg dig;
    wire walk_left;
    wire walk_right;
    wire aaah;
    wire digging;

    top_module dut(
        .clk(clk),
        .areset(areset),
        .bump_left(bump_left),
        .bump_right(bump_right),
        .ground(ground),
        .dig(dig),
        .walk_left(walk_left),
        .walk_right(walk_right),
        .aaah(aaah),
        .digging(digging)
    );

    initial begin
        clk = 0;
        forever #10 clk = ~clk;
    end

    initial begin
        // Test Case Initialization
        areset = 1; bump_left = 0; bump_right = 0; ground = 1; dig = 0;
        #20;

        areset = 0;
        // Test initial walking left condition
        if (walk_left !== 1 || walk_right !== 0 || aaah !== 0 || digging !== 0) begin
            $display("Test failed at Initial walking left condition");
            $finish;
        end

        // Test switch direction when bumped on the left
        bump_left = 1;
        #20;
        if (walk_left !== 0 || walk_right !== 1) begin
            $display("Test failed when bumped on left");
            $finish;
        end
        bump_left = 0;

        // Continue walking right and hit right bump
        bump_right = 1;
        #20;
        if (walk_left !== 1 || walk_right !== 0) begin
            $display("Test failed when bumped on right");
            $finish;
        end
        bump_right = 0;

        // Test Falling
        ground = 0;
        #20;
        if (aaah !== 1) begin
            $display("Test failed on fall initiation");
            $finish;
        end

        // Test ground comes back in less than splattering time
        #180;
        ground = 1;
        #20;
        if (walk_left !== 1 || walk_right !== 0 || aaah !== 0) begin
            $display("Test failed when ground comes back");
            $finish;
        end

        // Test dig start condition
        dig = 1;
        #20;
        if (digging !== 1 || aaah !== 0 || walk_left !== 0 || walk_right !== 0) begin
            $display("Test failed when start digging");
            $finish;
        end

        // End of digging due to ground disappears
        ground = 0;
        dig = 0;
        #20;
        if (aaah !== 1 || digging !== 0) begin
            $display("Test failed when digging ends due to no ground");
            $finish;
        end

        // Ground reappears with previous direction maintained
        ground = 1;
        #20;
        if (walk_left !== 1 || walk_right !== 0 || aaah !== 0) begin
            $display("Test failed when ground reappears expecting left walk");
            $finish;
        end

        // Test splatter condition
        ground = 0;
        #220;  // Fall for more than 20 cycles, splatter time
        ground = 1;
        #20;
        if (walk_left !== 0 || walk_right !== 0 || aaah !== 0 || digging !== 0) begin
            $display("Test failed on splatter event");
            $finish;
        end

        $display("All test cases passed");
    end
endmodule

