module top_module_tb;

    reg clk;
    reg reset;
    reg x;
    wire z;

    top_module DUT (
        .clk(clk),
        .reset(reset),
        .x(x),
        .z(z)
    );

    // Clock generation
    initial begin
        clk = 0;
        forever #5 clk = ~clk;  // Generate a clock with period 10 ns
    end

    // Stimulus: reset, test input sequence
    initial begin
        reset = 1'b1;  // Assert synchronous reset
        x = 0;
        #15;            // Wait for 15 ns, hold reset for at least one positive edge of clk
        reset = 1'b0;

        // Test sequence
        @(posedge clk); #1; x = 0;  // 000 -> 000
        check_outputs(3'b000, 0);

        @(posedge clk); #1; x = 1;  // 000 -> 001
        check_outputs(3'b001, 0);

        @(posedge clk); #1; x = 0;  // 001 -> 001
        check_outputs(3'b001, 0);

        @(posedge clk); #1; x = 1;  // 001 -> 100
        check_outputs(3'b100, 1);

        @(posedge clk); #1; x = 1;  // 100 -> 100
        check_outputs(3'b100, 1);

        @(posedge clk); #1; x = 0;  // 100 -> 011
        check_outputs(3'b011, 1);

        @(posedge clk); #1; x = 1;  // 011 -> 010
        check_outputs(3'b010, 0);

        @(posedge clk); #1; x = 0;  // 010 -> 010
        check_outputs(3'b010, 0);

        @(posedge clk); #1; x = 1;  // 010 -> 001
        check_outputs(3'b001, 0);

        @(posedge clk); #1; x = 0;  // 011 -> 001
        check_outputs(3'b001, 0);

        // All test cases passed
        if (passed_tests == 10) begin
            $display("all test cases passed");
        end

        $finish;
    end

    reg [2:0] current_state;
    integer passed_tests = 0;

    // Check utility function
    task check_outputs;
        input [2:0] expected_state;
        input expected_z;
        if (current_state == expected_state && z == expected_z) begin
            passed_tests = passed_tests + 1;
        end
    endtask

    // State monitoring
    always @(posedge clk) begin
        if (reset) begin
            current_state <= 3'b000;
        end
        else begin
            case(current_state)
                3'b000: current_state <= (x ? 3'b001 : 3'b000);
                3'b001: current_state <= (x ? 3'b100 : 3'b001);
                3'b010: current_state <= (x ? 3'b001 : 3'b010);
                3'b011: current_state <= (x ? 3'b010 : 3'b001);
                3'b100: current_state <= (x ? 3'b100 : 3'b011);
                default: current_state <= 3'b000;
            endcase
        end
    end

endmodule

