module testbench;
    reg clk, areset, bump_left, bump_right, ground, dig;
    wire walk_left, walk_right, aaah, digging;
    reg test_failed; // flag to check if any test case failed

    top_module DUT(
        .clk(clk),
        .areset(areset),
        .bump_left(bump_left),
        .bump_right(bump_right),
        .ground(ground),
        .dig(dig),
        .walk_left(walk_left),
        .walk_right(walk_right),
        .aaah(aaah),
        .digging(digging)
    );

    initial begin
        clk = 0;
        forever #5 clk = ~clk;
    end

    initial begin
        test_failed = 0; // Initialize test failure flag
        areset = 1; #10;
        areset = 0; #10;

        // case 1: Walk left to right on bump on the left
        bump_left = 1; bump_right = 0; ground = 1; dig = 0; #10;
        if (walk_right !== 1 || walk_left !== 0 || aaah !== 0 || digging !== 0) begin
            $display("Test Case 1 Failed");
            test_failed = 1; // Set test failure flag
        end
        bump_left = 0; #10;

        // case 2: Walking right and hit right bump
        bump_right = 1; #10;
        if (walk_left !== 1 || walk_right !== 0 || aaah !== 0 || digging !== 0) begin
            $display("Test Case 2 Failed");
            test_failed = 1;
        end
        bump_right = 0; #10;

        // case 3: Falling when there's no ground
        ground = 0; #10;
        if (aaah !== 1 || digging !== 0) begin
            $display("Test Case 3 Failed");
            test_failed = 1;
        end

        // case 4: Return to walking left when ground reappears
        ground = 1; #10;
        if (walk_left !== 1 || walk_right !== 0 || aaah !== 0 || digging !== 0) begin
            $display("Test Case 4 Failed");
            test_failed = 1;
        end

        // case 5: Check digging (start from walking right)
        bump_left = 1; bump_right = 0; ground = 1; #10;
        dig = 1; #10;
        if (digging !== 1 || aaah !== 0) begin
            $display("Test Case 5 Failed");
            test_failed = 1;
        end

        // case 6: Stop digging on no ground
        ground = 0; dig = 0; #10;
        if (aaah !== 1 || digging !== 0) begin
            $display("Test Case 6 Failed");
            test_failed = 1;
        end

