module testbench;
    reg clk;
    reg [7:0] in;
    wire [7:0] anyedge;
    reg [7:0] expected_anyedge;
    reg [7:0] previous_in;

    integer i;
    integer error_count;

    // Instantiate the Device Under Test (DUT)
    top_module DUT (
        .clk(clk),
        .in(in),
        .anyedge(anyedge)
    );

    // Clock generation
    initial begin
        clk = 0;
        forever #5 clk = ~clk; // Generate a clock with 10 ns period
    end

    // Test stimulus
    initial begin
        // Initialize
        in = 8'b00000000;
        previous_in = 8'b00000000;
        error_count = 0;

        // Wait for the clock to stabilize
        @(posedge clk);

        // Test Case 1: No transitions
        in = 8'b00000000;
        expected_anyedge = 8'b00000000;
        @(posedge clk) check_anyedge();

        // Test Case 2: Single transition 0->1 at one bit
        in = 8'b00000001;
        expected_anyedge = 8'b00000001;
        @(posedge clk) check_anyedge();

        // Test Case 3: Multiple transitions 0->1
        in = 8'b11001100;
        expected_anyedge = 8'b11001101;
        @(posedge clk) check_anyedge();

        // Test Case 4: 1->0 transitions should not affect
        in = 8'b00000000;
        expected_anyedge = 8'b00000000;
        @(posedge clk) check_anyedge();

        // Test Case 5: Alternating 0s and 1s
        in = 8'b10101010;
        expected_anyedge = 8'b00101010;
        @(posedge clk) check_anyedge();

        // Final report
        if (error_count == 0) begin
            $display("all test cases passed");
        end

        // Finish simulation
        $finish;
    end

    // Task to compare output against expected output
    task check_anyedge;
        begin
            @(posedge clk) begin
                // Compare output with expected output
                if (anyedge !== expected_anyedge) begin
                    $display("Test failed at time %t, Input: %b, Output: %b, Expected: %b",
                             $time, in, anyedge, expected_anyedge);
                    error_count = error_count + 1;
                end
                previous_in = in;
            end
        end
    endtask

endmodule

