module testbench;
    reg clk, reset, ena;
    wire pm;
    wire [7:0] hh, mm, ss;
    reg [7:0] expected_hh, expected_mm, expected_ss;
    reg expected_pm;
    integer errors = 0;

    top_module UUT (
        .clk(clk),
        .reset(reset),
        .ena(ena),
        .pm(pm),
        .hh(hh),
        .mm(mm),
        .ss(ss)
    );

    // Clock generation
    always #5 clk = ~clk; // simulate a fast-running clock

    // Initialize test setup
    initial begin
        clk = 0; reset = 1; ena = 0;
        #20 reset = 0;
    end

    // Test cases
    task check_output;
        if (pm !== expected_pm || hh !== expected_hh || mm !== expected_mm || ss !== expected_ss) begin
            errors = errors + 1;
            $display("Test Case Failed: Time %02h:%02h:%02h %s", hh, mm, ss, pm ? "PM" : "AM");
        end
    endtask

    // Run Tests 
    initial begin
        // Reset condition test
        #10;
        expected_pm = 0; expected_hh = 8'h12; expected_mm = 8'h00; expected_ss = 8'h00;
        check_output();

        // Test midnight to 1 second AM
        ena = 1;
        #10;
        expected_ss = 8'h01;
        check_output();

        // Test 1 second to 59 seconds AM
        repeat (58) begin
            #10 expected_ss = expected_ss + 1;
            check_output();
        end

        // Test 59 seconds to 1 minute AM
        #10;
        expected_ss = 8'h00; expected_mm = 8'h01;
        check_output();

        // Test 59 minutes to 1 hour AM
        expected_ss = 8'h00; expected_mm = 8'h59;
        check_output();
        #10;
        expected_mm = 8'h00; expected_hh = 8'h02;
        check_output();

        // Test 11:59 AM to 12:00 PM (noon)
        expected_hh = 8'h11; expected_mm = 8'h59; expected_ss = 8'h59;
        check_output();
        #10;
        expected_hh = 8'h12; expected_mm = 8'h00; expected_ss = 8'h00; expected_pm = 1;
        check_output();

        // Test 11:59 PM to 12:00 AM (midnight)
        expected_hh = 8'h11; expected_mm = 8'h59; expected_ss = 8'h59; expected_pm = 1;
        check_output();
        #10;
        expected_hh = 8'h12; expected_mm = 8'h00; expected_ss = 8'h00; expected_pm = 0;
        check_output();

        // Final check
        if (errors == 0) $display ("all test cases passed");

        // End simulation
        $finish;
    end
endmodule

