module testbench();
    reg clk, in, reset;
    wire out;
    integer i;

    // Instance of top_module
    top_module DUT (
        .clk(clk),
        .in(in),
        .reset(reset),
        .out(out)
    );

    // Clock generation
    initial begin
        clk = 0;
        forever #5 clk = ~clk; // Clock period of 10 ns
    end

    // Test cases
    initial begin
        reset = 1; #10; // Apply reset
        reset = 0;

        // Test Case 1: Reset functionality - Should be in State A with out = 0
        if (out !== 0) $display("Test Case 1 Failed - State A output error on reset");

        // Input pattern to explore each state transition according to the description
        in = 0; #10; // Stays in A, out should be 0
        if (out !== 0) $display("Test Case 2 Failed - State A self transition error");

        in = 1; #10; // Moves to B, out should be 0
        if (out !== 0) $display("Test Case 3 Failed - Transition to State B output error");

        in = 0; #10; // Moves to C from B, out should be 0
        if (out !== 0) $display("Test Case 4 Failed - Transition to State C output error");

        in = 0; #10; // Moves to A from C, out should be 0
        if (out !== 0) $display("Test Case 5 Failed - Transition to State A from C output error");

        in = 1; #10; // Moves to B from A, out should be 0
        if (out !== 0) $display("Test Case 6 Failed - Transition to State B from A output error");

        in = 1; #10; // Stays in B, out should be 0
        if (out !== 0) $display("Test Case 7 Failed - State B self transition error");

        in = 1; #10; // Moves to B from D, out should be 1
        if (out !== 1) $display("Test Case 8 Failed - Transition to State B from D output error");

        in = 0; #10; // Moves to C from D, out should be 1
        if (out !== 1) $display("Test Case 9 Failed - Transition to State C from D output error");

        in = 1; #10; // Moves to D from C, out should be 0
        if (out !== 1) $display("Test Case 10 Failed - Transition to State D output error");

        // Check if all test cases passed.
        for (i = 1; i <= 10; i = i + 1) begin
            if ($test$plusargs("Test Case " + itoa(i) + " Failed")) begin
                $finish;
            end
        end
        $display("all test cases passed");
        $finish;
    end

    // Convert integer to string
    function [8*10:1] itoa;
        input integer num;
        reg [8*10:1] str;
        reg [3:0] i;
        begin
            for (i = 0; i < 10; i = i+1) begin
                str[8*i +: 8] = (num % 10) + "0";
                num = num / 10;
            end
            itoa = str;
        end
    endfunction
endmodule

