module testbench;
    reg clk, j, k;
    reg Qold;
    wire Q;

    // Instantiate the Device Under Test (DUT)
    top_module dut(
        .clk(clk),
        .j(j),
        .k(k),
        .Q(Q)
    );

    // Clock generation
    initial begin
        clk = 0;
        forever #5 clk = ~clk;  // Generating clock with period 10ns
    end

    // Stimulus here
    initial begin
        // Initialize inputs
        j = 0;
        k = 0;
        Qold = Q;

        // Set up monitoring
        $monitor("At time %t, clk = %b, j = %b, k = %b -> Q = %b", $time, clk, j, k, Q);

        // Apply test cases
        @(posedge clk); #1 j = 0; k = 0; Qold = Q;  // JK = 00, Expect Q = Qold
        @(posedge clk); #1 if(Q != Qold) $finish; 

        @(posedge clk); #1 j = 0; k = 1;  // JK = 01, Expect Q = 0
        @(posedge clk); #1 if(Q != 0) $finish;

        @(posedge clk); #1 j = 1; k = 0;  // JK = 10, Expect Q = 1
        @(posedge clk); #1 if(Q != 1) $finish;

        @(posedge clk); #1 j = 1; k = 1; Qold = Q;  // JK = 11, Expect Q = ~Qold
        @(posedge clk); #1 if(Q != ~Qold) $finish;

        // If reached here, all tests passed
        $display("all test cases passed");

        // Finish simulation
        $finish;
    end
endmodule

`timescale 1ns / 1ps

module testbench();

    reg [254:0] in;
    wire [7:0] out;
    integer i, error_count;
    reg [7:0] expected_out;

    top_module dut (
        .in(in),
        .out(out)
    );

    initial begin
        error_count = 0;
        // Test case 1: All bits are 0
        in = 255'b0;
        expected_out = 0;
        #10;
        if (out !== expected_out) begin
            error_count = error_count + 1;
        end

        // Test case 2: All bits are 1
        in = {255{1'b1}};
        expected_out = 255;
        #10;
        if (out !== expected_out) begin
            error_count = error_count + 1;
        end

        // Test case 3: Alternate bits
        in = 255';
        expected_out = 128;
        #10;
        if (out !== expected_out) begin
            error_count = error_count + 1;
        end

        // Test case 4: Single '1' in the middle
        in = 255'b0;
        in[127] = 1;
        expected_out = 1;
        #10;
        if (out !== expected_out) begin
            error_count = error_count + 1;
        end

        // Test case 5: Random pattern
        in = 255'h123456789ABCDEF0123456789ABCDEF0123456789ABCDEF0123456789ABCDEF0123456789ABCDEF0123;
        expected_out = $countones(in);
        #10;
        if (out !== expected_out) begin
            error_count = error_count + 1;
        end

        // Display results of the test
        if (error_count == 0) begin
            $display("all test cases passed");
        end

        $finish;
    end
endmodule

