module testbench;
    reg clk, reset;
    reg [31:0] in;
    wire [31:0] out;

    // Instantiating the Device Under Test (DUT)
    top_module DUT (
        .clk(clk),
        .reset(reset),
        .in(in),
        .out(out)
    );

    // Clock generation
    initial begin
        clk = 0;
        forever #5 clk = ~clk; // Clock period of 10ns
    end

    // Test vectors
    initial begin
        reset = 1;
        in = 32'h0;
        #10; // Wait for the reset condition

        reset = 0;
        in = 32'hFFFFFFFF; // Set all bits to '1'
        #10;

        in = 32'h0; // All bits to '0', should capture this change
        #10;

        // Check if output captures falling edge for all bits
        if (out != 32'hFFFFFFFF) begin
            $display("Test case failed.");
            $finish;
        end

        reset = 1; // Resetting to clear the captured edges
        #10;
        if (out != 32'h0) begin
            $display("Test case failed.");
            $finish;
        end

        reset = 0;
        in = 32'hAAAA5555; // Alternate bits '1' and '0'
        #10;
        in = 32'h5555AAAA; // Alternate bits '0' and '1'
        #10;

        // Verify if out captures only the bits that had a falling edge
        if (out != 32'hAAAA0000) begin
            $display("Test case failed.");
            $finish;
        end

        // All test cases passed
        $display("all test cases passed");
        $finish; // Ensuring the simulation stops
    end
endmodule

