module testbench;

    reg [4:1] x;
    wire f;
    reg [4:0] test_vectors[0:15]; 
    integer i;
    reg expected_f;
    reg failed = 0;

    // Instantiate the Device Under Test (DUT)
    top_module dut (
        .x(x),
        .f(f)
    );

    initial begin
        // The test vector includes some don't-care conditions as 'x' which we choose to ignore in verification
        // Fill the test vectors with input patterns and expected output
        test_vectors[0] = 5'b00000; // x = 0000 -> d 
        test_vectors[1] = 5'b00010; // x = 0001 -> 0
        test_vectors[2] = 5'b00111; // x = 0011 -> d
        test_vectors[3] = 5'b00100; // x = 0010 -> d
        test_vectors[4] = 5'b01000; // x = 0100 -> 0
        test_vectors[5] = 5'b0101x; // x = 0101 -> d
        test_vectors[6] = 5'b01111; // x = 0111 -> 1
        test_vectors[7] = 5'b01100; // x = 0110 -> 0
        test_vectors[8] = 5'b11001; // x = 1100 -> 1
        test_vectors[9] = 5'b11011; // x = 1101 -> 1
        test_vectors[10] = 5'b1111x; // x = 1111 -> d
        test_vectors[11] = 5'b1110x; // x = 1110 -> d
        test_vectors[12] = 5'b10001; // x = 1000 -> 1
        test_vectors[13] = 5'b10011; // x = 1001 -> 1
        test_vectors[14] = 5'b10100; // x = 1010 -> 0
        test_vectors[15] = 5'b1010x; // x = 1011 -> d

        for (i = 0; i < 16; i = i + 1) begin
            {x, expected_f} = test_vectors[i];
            #10;
            // Check result only when expected_f is not 'x'
            if (expected_f !== 1'bx && f !== expected_f) begin
                $display("Test failed for input %b, expected %b, got %b", x, expected_f, f);
                failed = 1;
            end
        end
        if (!failed)
            $display("all test cases passed");
        #10 $finish;
    end

endmodule

