module testbench;

    // Inputs to the Device Under Test
    reg clk;
    reg [7:0] in;
    reg reset;

    // Outputs from the Device Under Test
    wire [23:0] out_bytes;
    wire done;

    // Instantiate the Device Under Test (top_module)
    top_module DUT (
        .clk(clk),
        .in(in),
        .reset(reset),
        .out_bytes(out_bytes),
        .done(done)
    );

    // Clock generation
    initial begin
        clk = 0;
        forever #5 clk = ~clk; // 100MHz Clock
    end

    // Test stimuli
    initial begin
        // Initialize Inputs
        in = 0;
        reset = 1;

        // Reset the device
        #10;
        reset = 0;

        // Case 1: Test the FSM with input sequence
        // Waiting for in[3] = 1, then expecting 3 bytes
        @(posedge clk) in = 8'h2C;
        @(posedge clk) in = 8'h81; // First valid byte (in[3] = 1)
        @(posedge clk) in = 8'h09;
        @(posedge clk) in = 8'h6B;
        @(posedge clk) in = 8'h0D;
        @(posedge clk) in = 8'h8D;
        @(posedge clk) in = 8'h6D;
        @(posedge clk) in = 8'h12;
        @(posedge clk) in = 8'h01;
        @(posedge clk) in = 8'hD7;
        @(posedge clk) in = 8'h63;
        @(posedge clk) in = 8'h3D;
        @(posedge clk) in = 8'hED;
        @(posedge clk) in = 8'h8C;
        @(posedge clk) in = 8'hF9;
        @(posedge clk) in = 8'hCE;
        @(posedge clk) in = 8'hC5;
        @(posedge clk) in = 8'hAA; // Last input for last case

        // Complete the test after all cases processed
        #10;
        $finish;
    end

    // Check outputs and display result
    reg [3:0] test_cases = 0;
    always @(posedge done) begin
        if (out_bytes == (test_cases == 0 ? 24'h2C8109 :
                          test_cases == 1 ? 24'h6B0D8D :
                          test_cases == 2 ? 24'h6D1201 :
                          test_cases == 3 ? 24'hD763D :
                          test_cases == 4 ? 24'hED8CF9 : 24'h000000))
            test_cases = test_cases + 1;
    end

    initial begin
        @(posedge clk);
        #200; // Wait for all test cases to be processed
        if (test_cases == 5) $display("all test cases passed");
    end

endmodule

