module testbench;
    reg in;
    wire out;
    reg last_out;

    // Instantiate the Device Under Test (DUT)
    top_module dut (
        .in(in),
        .out(out)
    );

    // Test cases
    initial begin
        // Initialize the input
        in = 0;
        last_out = 0;

        // Applying test cases
        #5 in = 0;    // Test case 0: Check for input = 0
        #5 if (out !== 0) $display("Test case 0 failed");

        #5 in = 1;    // Test case 1: Check for input = 1
        #5 if (out !== 1) $display("Test case 1 failed");

        #5 in = 0;    // Test case 2: Back to input = 0
        #5 if (out !== 0) $display("Test case 2 failed");

        #5 in = 1;    // Test case 3: Back to input = 1
        #5 if (out !== 1) $display("Test case 3 failed");

        #10; // Wait for last changes to propagate

        // Check if any test cases failed
        if (last_out == 1 && out == 0) begin
            $display("all test cases passed");
        end else begin
            $display("some test cases failed");
        end

        #5 $finish; // Finish simulation
    end

    // Track the last value of 'out' to detect falling edge manually
    always @(out) begin
        last_out <= out;
    end

endmodule

