module testbench;
    // Testbench variables
    reg x, y;
    wire z;

    // Instantiate DUT
    top_module dut(
        .x(x),
        .y(y),
        .z(z)
    );

    // Variable to track test status
    reg all_tests_passed;

    // Initial block that handles test cases
    initial begin
        all_tests_passed = 1'b1; // Assume all tests pass initially

        // Test Case 1
        x = 0; y = 0;
        #10; // Wait 10ns for outputs to settle
        if (z !== ((x ^ y) & x)) begin
            all_tests_passed = 1'b0;
            $display("Test case failed for x=0, y=0. Expected %b, got %b", ((x ^ y) & x), z);
        end

        // Test Case 2
        x = 0; y = 1;
        #10;
        if (z !== ((x ^ y) & x)) begin
            all_tests_passed = 1'b0;
            $display("Test case failed for x=0, y=1. Expected %b, got %b", ((x ^ y) & x), z);
        end

        // Test Case 3
        x = 1; y = 0;
        #10;
        if (z !== ((x ^ y) & x)) begin
            all_tests_passed = 1'b0;
            $display("Test case failed for x=1, y=0. Expected %b, got %b", ((x ^ y) & x), z);
        end

        // Test Case 4
        x = 1; y = 1;
        #10;
        if (z !== ((x ^ y) & x)) begin
            all_tests_passed = 1'b0;
            $display("Test case failed for x=1, y=1. Expected %b, got %b", ((x ^ y) & x), z);
        end

        // Check if all tests passed
        if (all_tests_passed) begin
            $display("all test cases passed");
        end

        // End simulation
        $finish;
    end

endmodule

