module top_module_tb;

    // Declare inputs as regs and outputs as wires
    reg [99:0] a, b;
    reg sel;
    wire [99:0] out;

    // Instantiate the Unit Under Test (UUT)
    top_module uut (
        .a(a), 
        .b(b), 
        .sel(sel), 
        .out(out)
    );

    // Test cases
    integer i; 
    reg [99:0] expected; 
    reg all_passed;

    initial begin
        // Initialize Inputs
        a = 0; b = 0; sel = 0; all_passed = 1;

        // Stimuli
        for (i = 0; i < 100; i = i + 1) begin
            a = $random;
            b = $random;
            sel = 0;
            expected = a;
            #10;
            if (out !== expected) all_passed = 0;

            sel = 1;
            expected = b;
            #10;
            if (out !== expected) all_passed = 0;
        end

        // Check all test cases
        if (all_passed) $display("all test cases passed");

        // Finish testbench
        $finish;
    end

endmodule

