module testbench;
    reg [7:0] a, b;
    wire [7:0] s;
    wire overflow;

    // Instantiate the Device Under Test (DUT)
    top_module dut (
        .a(a),
        .b(b),
        .s(s),
        .overflow(overflow)
    );

    // Test cases
    initial begin
        $monitor("Time:%t, a=%d, b=%d, s=%d, overflow=%b", $time, a, b, s, overflow);

        // Initialize inputs
        a = 0; b = 0;

        // Apply test vectors
        #10 a = 8'd127; b = 8'd1;      // Overflow test
        #10 a = 8'd127; b = 8'd127;    // Overflow test
        #10 a = -8'd128; b = -8'd1;    // Overflow test
        #10 a = 8'd50; b = 8'd70;      // Normal addition
        #10 a = 8'd0; b = -8'd1;       // Negative test
        #10 a = -8'd50; b = 8'd50;     // Result zero
        #10 a = -8'd50; b = 8'd70;     // Negative and positive
        #10 a = 8'd70; b = -8'd50;     // Positive and negative

        #10 $finish;
    end

    // Check pass condition
    integer errors = 0;
    always @(s or overflow) begin
        #1; // Slight delay to ensure all calculations are done
        if ((a == 8'd127 && b == 8'd1 && (s != 8'd128 || overflow != 1'b1)) ||
            (a == 8'd127 && b == 8'd127 && (s != 8'hFE || overflow != 1'b1)) ||
            (a == -8'd128 && b == -8'd1 && (s != 8'h7F || overflow != 1'b1)) ||
            (a == 8'd50 && b == 8'd70 && (s != 8'd120 || overflow != 1'b0)) ||
            (a == 8'd0 && b == -8'd1 && (s != 8'hFF || overflow != 1'b0)) ||
            (a == -8'd50 && b == 8'd50 && (s != 8'd0 || overflow != 1'b0)) ||
            (a == -8'd50 && b == 8'd70 && (s != 8'd20 || overflow != 1'b0)) ||
            (a == 8'd70 && b == -8'd50 && (s != 8'd20 || overflow != 1'b0))) begin
            errors = errors + 1;
            $display("Test case failed: a=%d, b=%d, expected s=?, overflow=?", a, b);
        end
    end

    // Check if all tests passed at the end
    initial begin
        #100; // Wait until all test cases have been processed
        if (errors == 0) begin
            $display("all test cases passed");
        end
    end

endmodule

