module testbench;
    reg a, b, cin;
    wire cout, sum;
    integer errors = 0;

    // Instantiate the Device Under Test (DUT)
    top_module dut (
        .a(a),
        .b(b),
        .cin(cin),
        .cout(cout),
        .sum(sum)
    );

    // Procedure to apply test vectors and check the responses
    task apply_and_check;
        input a_, b_, cin_;
        input expected_cout, expected_sum;
        begin
            // Apply input
            a = a_;
            b = b_;
            cin = cin_;
            #10; // Wait for the values to propagate

            // Check output
            if (cout !== expected_cout || sum !== expected_sum) begin
                $display("Failure: for inputs a=%b, b=%b, cin=%b, expected cout=%b, sum=%b but got cout=%b, sum=%b.", 
                          a, b, cin, expected_cout, expected_sum, cout, sum);
                errors = errors + 1;
            end
        end
    endtask

    // Main simulation
    initial begin
        // Test Cases
        apply_and_check(0, 0, 0, 0, 0);
        apply_and_check(0, 0, 1, 0, 1);
        apply_and_check(0, 1, 0, 0, 1);
        apply_and_check(0, 1, 1, 1, 0);
        apply_and_check(1, 0, 0, 0, 1);
        apply_and_check(1, 0, 1, 1, 0);
        apply_and_check(1, 1, 0, 1, 0);
        apply_and_check(1, 1, 1, 1, 1);

        // Check if all test cases passed
        #10;
        if (errors == 0) begin
            $display("all test cases passed");
        end
        $finish;
    end
endmodule

