        .out_or(out_or),
        .out_xor(out_xor)
    );

    initial begin
        // Test Case 1: All zeros
        in = 4'b0000;
        #10;
        if (out_and !== 1'b0 || out_or !== 1'b0 || out_xor !== 1'b0) begin
            $display("Test Case 1 Failed");
            $finish;
        end

        // Test Case 2: All ones
        in = 4'b1111;
