        // Test Case 3: Mixed input
        in = 4'b1010;
        #10;
        if (out_and !== 1'b0 || out_or !== 1'b1 || out_xor !== 1'b0) begin
            $display("Test Case 3 Failed");
            $finish;
        end

        // Test Case 4: Other mixed input
        in = 4'b1001;
        #10;
        if (out_and !== 1'b0 || out_or !== 1'b1 || out_xor !== 1'b0) begin
            $display("Test Case 4 Failed");
            $finish;
