        // Apply reset
        #10;
        reset = 0;
        s = 1; // Move out of state A

        // Test vector definition
        test_vector[0] = 3'b000;  // 0 ones
        test_vector[1] = 3'b001;  // 1 one
        test_vector[2] = 3'b010;  // 1 one
        test_vector[3] = 3'b011;  // 2 ones
        test_vector[4] = 3'b100;  // 1 one
        test_vector[5] = 3'b101;  // 2 ones
        test_vector[6] = 3'b110;  // 2 ones
        test_vector[7] = 3'b111;  // 3 ones

        // Testing all cases
        for (i = 0; i < 8; i = i + 1) begin
            // Apply test vector
            w = test_vector[i][0];
            #10 w = test_vector[i][1];
            #10 w = test_vector[i][2];
            #10;
            // check result
            count = test_vector[i][0] + test_vector[i][1] + test_vector[i][2]; 
            if ((count == 2 && z == 1) || (count != 2 && z == 0)) begin
                passed_cases = passed_cases + 1;
