module testbench;
    reg clk, in, reset;
    wire done;

    top_module DUT (
        .clk(clk),
        .in(in),
        .reset(reset),
        .done(done)
    );

    // Clock generation
    initial begin
        clk = 0;
        forever #5 clk = ~clk; // Clock period of 10 ns
    end

    // Test cases
    initial begin
        reset = 1; 
        #20; // Hold reset high for some time
        reset = 0;

        // Test Case 1: Correct sequence
        // Start bit (0), 8-bit data (10110011), Stop bit (1)
        send_byte(8'b10110011); 
        check_done(1);

        // Test Case 2: Incorrect stop bit
        // Start bit (0), 8-bit data (01010101), Wrong Stop bit (0), Correct Stop bit (1)
        send_byte_with_error(8'b01010101);
        check_done(0);

        // Test Case 3: Multiple bytes
        // Byte 1: Start (0), 8-bit data (11110000), Stop (1)
        // Byte 2: Start (0), 8-bit data (00001111), Stop (1)
        send_byte(8'b11110000); 
        check_done(1);
        send_byte(8'b00001111); 
        check_done(1);

        // After all test cases
        $display("all test cases passed");
        $finish;
    end

    // Task to send a byte 
    task send_byte;
        input [7:0] data;
        integer i;

        begin
            @(negedge clk); in = 0; // Start bit
            for (i = 0; i < 8; i = i + 1) begin
                @(negedge clk); in = data[i]; // Data bits, LSB first
            end
            @(negedge clk); in = 1; // Stop bit
        end
    endtask

    // Task to send byte with incorrect stop bit followed by a correct stop bit
    task send_byte_with_error;
        input [7:0] data;
        integer i;

        begin
            @(negedge clk); in = 0; // Start bit
            for (i = 0; i < 8; i = i + 1) begin
                @(negedge clk); in = data[i]; // Data bits
            end
            @(negedge clk); in = 0; // Wrong Stop bit
            @(negedge clk); in = 1; // Correct stop bit
        end
    endtask

    // Task to check done signal
    task check_done;
        input expected;

        begin
            @(posedge clk); // wait for a clock edge to check the done signal
            if(done !== expected) begin
                $display("Test failed at time %t", $time);
                $finish;
            end
        end
    endtask
endmodule

