module tb_top_module;

    // Inputs
    reg clock;
    reg a;

    // Outputs
    wire p;
    wire q;

    // Instantiate the Unit Under Test (UUT)
    top_module uut (
        .clock(clock), 
        .a(a), 
        .p(p), 
        .q(q)
    );

    // Variables
    reg [7:0] expected_p; // Store expected p values
    reg [7:0] expected_q; // Store expected q values
    reg [7:0] actual_p;   // Store actual p values captured from DUT
    reg [7:0] actual_q;   // Store actual q values captured from DUT
    integer i;            // Loop variable

    // Clock generation
    initial begin
        clock = 0;
        forever #5 clock = ~clock;
    end

    // Stimulus and capturing mechanism
    initial begin
        // Initialize Inputs
        a = 0;
        expected_p = 8'b00001111;
        expected_q = 8'b00001111;
        actual_p = 0;
        actual_q = 0;

        // Wait for reset to finish
        #(5*25); // wait for 125ns after reset to ensure DUT is ready
        @(posedge clock); a = 0; // Start of positive edge
        @(posedge clock); a = 0;
        @(posedge clock); a = 1;
        @(posedge clock); a = 0;
        @(posedge clock); a = 1;
        @(posedge clock); a = 0;
        @(posedge clock); a = 1;
        @(posedge clock); a = 0;

        // Capture outputs immediately after the last input is set
        for (i = 0; i < 8; i = i + 1) begin
            @(posedge clock);
            actual_p = (actual_p << 1) | p;
            actual_q = (actual_q << 1) | q;
        end

        // All comparisons done, check results
        if (actual_p === expected_p && actual_q === expected_q) begin
            $display("all test cases passed");
        end

        // Terminate the simulation
        $finish;
    end

endmodule

