module testbench;
    reg clk, areset, load, ena;
    reg [3:0] data;
    wire [3:0] q;
    integer i;

    // Instantiate the Device Under Test (DUT)
    top_module DUT (
        .clk(clk),
        .areset(areset),
        .load(load),
        .ena(ena),
        .data(data),
        .q(q)
    );

    // Clock generation
    initial begin
        clk = 0;
        forever #5 clk = ~clk; // Clock period of 10ns
    end

    // Test cases
    initial begin
        // Initialize all inputs
        areset = 0; load = 0; ena = 0; data = 4'b0000;

        // Reset the shift register
        #10;
        areset = 1; #10;
        areset = 0;
        if (q !== 4'b0000) begin
            $display("Reset failed, q = %b", q);
            $finish;
        end

        // Load data into the shift register
        data = 4'b1010; load = 1; #10;
        load = 0;
        if (q !== 4'b1010) begin
            $display("Load data failed, q = %b", q);
            $finish;
        end

        // Enable shift right
        ena = 1; #10;
        if (q !== 4'b0101) begin
            $display("Shift right failed, q = %b", q);
            $finish;
        end

        // Shift right with load priority
        data = 4'b1100; load = 1; ena = 1; #10;
        load = 0; ena = 0;
        if (q !== 4'b1100) begin
            $display("Load priority in shift failed, q = %b", q);
            $finish;
        end

        // Continue shift right
        #10; ena = 1; #10;
        if (q !== 4'b0110) begin
            $display("Continue shift right failed, q = %b", q);
            $finish;
        end

        // Final test to check edge cases
        #10; load = 1; data = 4'b0011; #10;
        load = 0; #20;
        ena = 1; #10;
        ena = 0; #10;
        if (q !== 4'b0001) begin
            $display("Edge case shift/load failed, q = %b", q);
            $finish;
        end

        // All test cases passed if reached here
        $display("all test cases passed");
    end

endmodule

