    initial begin
        clk = 0;
        forever #5 clk = ~clk;  // Toggle every 5 ns
    end

    // Test cases
    initial begin
        // Initialize inputs
        areset = 1;
        #10;
        areset = 0;
        predict_valid = 0;
        predict_taken = 0;
        train_mispredicted = 0;
        train_taken = 0;
        train_history = 0;

