        .o2(o2), 
        .o1(o1),
        .o0(o0)
    );

    // Initialize inputs
    initial begin
        vec = 3'b000; #10;
        vec = 3'b001; #10;
        vec = 3'b010; #10;
        vec = 3'b011; #10;
        vec = 3'b100; #10;
        vec = 3'b101; #10;
        vec = 3'b110; #10;
        vec = 3'b111; #10;
    end

