module tb_top_module;

    // Inputs
    reg clk;
    reg in;
    reg reset;

    // Outputs
    wire done;

    // Instantiate the Device Under Test (DUT)
    top_module dut(
        .clk(clk),
        .in(in),
        .reset(reset),
        .done(done)
    );

    // Clock generation
    always begin
        clk = 0; #5; clk = 1; #5;  // Clock period of 10 ns
    end

    // Test Stimulus
    initial begin
        // Initialize Inputs
        reset = 1; in = 1;  // Active high reset, start in idle
        #20;                
        reset = 0;          // Release reset
        #10;

        // Test case 1: Valid data transmission
        // Start bit (0), Data = 0xAF (10101111, lsb first), Stop bit (1)
        send_byte(8'b11111010, 1'b1);

        // Test case 2: Invalid stop bit
        // Start bit (0), Data = 0x55 (01010101, lsb first), Stop bit (0)
        send_byte(8'b10101010, 1'b0);
        #10; // Wait for the FSM to handle the wrong stop bit before the idle
        in = 1; // Idle state

        // Test case 3: Correct byte after invalid stop bit
        send_byte(8'b00000111, 1'b1);

        #20;  // Wait some time after the last test case

        // Check if all tests pass
        if (done === 1'b1) begin
            $display("all test cases passed");
        end
        #10;
        $finish; // Terminate simulation
    end

    // Task to simulate sending a byte
    task send_byte;
        input [7:0] data;
        input bit stop_bit;
        begin
            in = 0; // Start bit
            #10;
            for (int i = 0; i < 8; i = i + 1) begin
                in = data[i];  // Data bits
                #10;
            end
            in = stop_bit; // Stop bit
            #10;
            in = 1; // Go back to idle
            #10;
        end
    endtask

endmodule

