module testbench;
    // Inputs
    reg clk;
    reg reset;
    reg s;
    reg w;

    // Outputs
    wire z;

    // Instantiate the Unit Under Test (UUT)
    top_module uut (
        .clk(clk),
        .reset(reset),
        .s(s),
        .w(w),
        .z(z)
    );

    // Clock generation
    initial begin
        clk = 0;
        forever #5 clk = ~clk;
    end

    // Variable to monitor test cases
    integer test_count = 0;
    integer passed_count = 0;

    // Stimulus and monitoring
    initial begin
        // Initialize Inputs
        reset = 1;
        s = 0;
        w = 0;

        // Wait for reset
        @(posedge clk);
        reset = 0;

        // Test Case 1: Still in reset state
        test_count = test_count + 1;
        @(posedge clk);
        s = 0;
        repeat (3) @(posedge clk);
        if (z !== 0) $display("Test Case 1 Failed");
        else passed_count = passed_count + 1;

        // Test Case 2: Trigger state B and check output
        test_count = test_count + 1;
        s = 1;
        @(posedge clk);
        s = 0; w = 1;
        @(posedge clk);
        w = 0;
        @(posedge clk);
        w = 1;
        @(posedge clk);
        if (z !== 1) $display("Test Case 2 Failed");
        else passed_count = passed_count + 1;

        // Test Case 3: Maintain state B and incorrect sequence
        test_count = test_count + 1;
        w = 1;
        @(posedge clk);
        w = 0;
        @(posedge clk);
        w = 0;
        @(posedge clk);
        if (z !== 0) $display("Test Case 3 Failed");
        else passed_count = passed_count + 1;

        // Test Case 4: Correct sequence after several checks
        test_count = test_count + 1;
        w = 1; @(posedge clk);
        w = 0; @(posedge clk);
        w = 1; @(posedge clk);
        if (z !== 1) $display("Test Case 4 Failed");
        else passed_count = passed_count + 1;

        // Check all tests passed
        if (passed_count == test_count) $display("all test cases passed");

        // End simulation
        $finish;
    end
