        test_y[1] = 3'b000; test_x[1] = 1; test_Y0_expected[1] = 1'b1; test_z_expected[1] = 1'b0;
        test_y[2] = 3'b001; test_x[2] = 0; test_Y0_expected[2] = 1'b1; test_z_expected[2] = 1'b0;
