        test_y[5] = 3'b010; test_x[5] = 1; test_Y0_expected[5] = 1'b1; test_z_expected[5] = 1'b0;
        test_y[6] = 3'b011; test_x[6] = 0; test_Y0_expected[6] = 1'b1; test_z_expected[6] = 1'b1;
