module testbench;

    reg clk;
    reg reset;
    wire shift_ena;

    // Instantiate the DUT (Device Under Test)
    top_module DUT (
        .clk(clk),
        .reset(reset),
        .shift_ena(shift_ena)
    );

    // Clock generation
    initial begin
        clk = 0;
        forever #5 clk = ~clk; // Clock period of 10ns
    end

    // Test Stimulus
    initial begin
        reset = 1; // start with reset active
        #10;
        reset = 0; // deactivate reset
        #100; // run for some time to observe shift_ena when not reset

        // apply a reset again to see if the behavior repeats
        reset = 1;
        #10;
        reset = 0;

        #100;
        $finish;
    end

    // Asserting the correctness of shift_ena's behavior
    initial begin
        integer error_count;
        error_count = 0;

        // Wait until after the initial reset
        wait (reset === 1);
        #10; // once reset asserts wait for the next clock edge

        // Checking for four cycles of shift_ena high
        if (shift_ena !== 1) begin
            $display("Error: shift_ena should be 1 after reset");
            error_count = error_count + 1;
        end
        #10;
        if (shift_ena !== 1) begin
            $display("Error: shift_ena should remain 1 on the second cycle");
            error_count = error_count + 1;
        end
        #10;
        if (shift_ena !== 1) begin
            $display("Error: shift_ena should remain 1 on the third cycle");
            error_count = error_count + 1;
        end
        #10;
        if (shift_ena !== 1) begin
            $display("Error: shift_ena should remain 1 on the fourth cycle");
            error_count = error_count + 1;
        end
        #10;
        if (shift_ena !== 0) begin
            $display("Error: shift_ena should be 0 after four cycles");
            error_count = error_count + 1;
        end

        // Now shifting to after the second reset
        wait (reset === 1);
        #10;
        repeat(4) begin
            #10;
            if (shift_ena !== 1) begin
                $display("Error: shift_ena should be 1 for four cycles after every reset");
                error_count = error_count + 1;
            end
        end
        #10;
        if (shift_ena !== 0) begin
            $display("Error: shift_ena should be 0 after subsequent four cycles");
            error_count = error_count + 1;
        end

        if (error_count == 0) begin
            $display("all test cases passed");
        end
    end

