module testbench;

    reg clk, load;
    reg [511:0] data;
    wire [511:0] q;
    top_module DUT(clk, load, data, q);

    integer i;

    // Generate clock
    initial begin
        clk = 0;
        forever #5 clk = ~clk; // 100 MHz clock, 10 ns period (half period is 5 ns)
    end

    // Procedure to load initial values
    task load_data;
        input [511:0] data_value;
        begin
            load = 1;
            data = data_value;
            @(posedge clk);
            load = 0;
        end
    endtask

    // Check for test failure
    reg test_failed;
    initial test_failed = 0;

    // Compare function
    task check_output;
        input [511:0] expected_output;
        begin
            if (q !== expected_output) begin
                test_failed = 1;
            end
        end
    endtask

    // Test sequence
    initial begin
        // Reset
        load_data(512'd0);

        // Test case 1: Alternating pattern and verifying boundaries
        load_data(512'hAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAA);
        @(posedge clk);
        check_output(512'h5555555555555555555555555555555555555555555555555555555555555555);

        // Test case 2: Single cell active in the middle
        load_data(512'd0);
        data[256] = 1;
        load_data(data);
        @(posedge clk);
        check_output(512'b0 | (1'b1 << 255) | (1'b1 << 257));

        // Test case 3: All cells active
        load_data({512{1'b1}});
        @(posedge clk);
        check_output({512{1'b0}});

        // Test case 4: Maximum activity except boundaries
        data = 512'b0;
        data[510:1] = {510{1'b1}};
        load_data(data);
        @(posedge clk);
        check_output(512'b0 | ({510{1'b0}} << 1));

        // Check if all tests passed
        if (!test_failed) begin
            $display("all test cases passed");
        end

        $finish;
    end

endmodule

