module tb_top_module;

reg clk;
reg resetn;
reg [3:1] r;
wire [3:1] g;

top_module DUT(
    .clk(clk),
    .resetn(resetn),
    .r(r),
    .g(g)
);

initial begin
    clk = 0;
    forever #5 clk = ~clk; // Clock generation with 10ns period
end

initial begin
    // Initialize signals
    resetn = 0; r = 3'b000;
    #15 resetn = 1; // Release reset after 15ns

    // State A tests
    #10 r = 3'b000; // Stay in A
    #10 checkState('h0, "Test A1 (stay)");

    r = 3'b100; // A to B
    #10 checkState('h1, "Test A to B");

    r = 3'b010; // B to A (higher priority ignored if not initial request)
    #10 checkState('h0, "Test B to C");

    r = 3'b001; // A to D, lower priority
    #10 checkState('h4, "Test A to D");

    // State B tests
    r = 3'b100; 
    #20 r = 3'b000; // B to A
    #10 checkState('h0, "Test B to A (release)");

    // State C tests
    r = 3'b010; 
    #20 r = 3'b000; // C to A
    #10 checkState('h0, "Test C to A (release)");

    // Test reset functionality
    #10 resetn = 0; #10 resetn = 1; // Assert reset and then release
    #10 r = 3'b000;
    #10 checkState('h0, "Reset test");

    #100;
    if (all_tests_passed)
        $display("all test cases passed");
    $finish;
end

integer test_counter = 0;
integer fail_counter = 0;
reg all_tests_passed = 1;

task checkState;
    input integer expected;
    input string testname;
    begin
        test_counter = test_counter + 1;
        if (expected !== {g[1], g[2], g[3]}) begin
            fail_counter = fail_counter + 1;
            all_tests_passed = 0;
            $display("%s failed: Expected %b, got %b", testname, expected, {g[1], g[2], g[3]});
        end
    end
endtask

