        if (q !== 4'b0000) $display("Test case failed at initial reset");

        // Monitor counter value for 10 cycles
        repeat(10) begin
            @(posedge clk); // Wait for a positive edge of the clock
            #1; // Small delay after clock edge to ensure q is stable
            if (q !== ($time/10) % 10) $display("Test case failed at count %d with q = %d", $time/10, q);
        end

        // Test reset functionality while running
        @(posedge clk);
        #1; reset = 1;
        @(posedge clk);
        #1; reset = 0;

        @(posedge clk);
        #1;
        if (q !== 4'b0000) $display("Test case failed after reset during counter operation");

        // If no failures, print success message
        if (!$test$plusargs("display_fail_only")) $display("all test cases passed");

        // Finish the test
        $finish;
    end
