module top_module_tb;

    // Inputs
    reg d;
    reg done_counting;
    reg ack;
    reg [9:0] state;

    // Outputs
    wire B3_next;
    wire S_next;
    wire S1_next;
    wire Count_next;
    wire Wait_next;
    wire done;
    wire counting;
    wire shift_ena;

    // Instantiate the DUT
    top_module DUT (
        .d(d),
        .done_counting(done_counting),
        .ack(ack),
        .state(state),
        .B3_next(B3_next),
        .S_next(S_next),
        .S1_next(S1_next),
        .Count_next(Count_next),
        .Wait_next(Wait_next),
        .done(done),
        .counting(counting),
        .shift_ena(shift_ena)
    );

    // Test Vectors
    initial begin
        // Initialize Inputs
        d = 0;
        done_counting = 0;
        ack = 0;
        state = 10'b0000000001; // State S

        // Apply test vectors
        #10 d = 1; // S to S1
            check("S1", state == 10'b0000000010);
        #10 d = 1; // S1 to S11
            check("S11", state == 10'b0000000100);
        #10 d = 0; // S11 to S110
            check("S110", state == 10'b0000001000);
        #10 d = 1; // S110 to B0, shift_ena should be 1 in B0 and the next states
            check("B0", state == 10'b0000010000);
            check_shift_ena();
        #10;   // B0 to B1
            check("B1", state == 10'b0000100000);
            check_shift_ena();
        #10;   // B1 to B2
            check("B2", state == 10'b0001000000);
            check_shift_ena();
        #10;   // B2 to B3
            check("B3", state == 10'b0010000000);
            check_shift_ena();
        #10;   // B3 to Count, counting should be 1 in Count
            check("Count", state == 10'b0100000000);
            check_counting();
        #10 done_counting = 1; // Count to Wait, done should be 1 in Wait
            check("Wait", state == 10'b1000000000);
            check_done();
        #10 ack = 1; // Wait to S
            check("S", state == 10'b0000000001);

        // Complete Test, Check for "all test cases passed"
        if (!test_fail)
            $display("all test cases passed");

        // End simulation
        $finish;
    end

    // Flags
    reg test_fail = 0;

    // Check Functions
    task check;
        input [8*12:1] testname;
        input condition;
        begin
            if (!condition) begin
                $display("Test %s failed", testname);
                test_fail = 1;
            end
        end
    endtask

    task check_shift_ena;
        begin
            if (!shift_ena) begin
                $display("shift_ena check failed in state where it should be high");
                test_fail = 1;
            end
        end
    endtask

    task check_counting;
        begin
            if (!counting) begin
                $display("counting check failed in Count state");
                test_fail = 1;
            end
        end
    endtask

    task check_done;
        begin
            if (!done) begin
                $display("done check failed in Wait state");
                test_fail = 1;
            end
        end
    endtask

endmodule

