module testbench;
    reg clk, reset;
    reg [7:0] in;
    wire [23:0] out_bytes;
    wire done;

    top_module DUT (
        .clk(clk),
        .in(in),
        .reset(reset),
        .out_bytes(out_bytes),
        .done(done)
    );

    // Clock generation
    initial begin
        clk = 0;
        forever #5 clk = ~clk;
    end

    // Initial stimulus
    initial begin
        // Initialize inputs
        reset = 1;
        in = 0;

        // Release reset
        #15 reset = 0;
        #10 in = 8'h81;  // Start of message byte
        #10 in = 8'h09;
        #10 in = 8'h6B;
        #10 in = 8'h0D;
        #10 in = 8'h8D;
        #10 in = 8'h6D;  // Another message start
        #10 in = 8'h12;
        #10 in = 8'h01;
        #10 in = 8'h76;
        #10 in = 8'h3D;
        #10 in = 8'hED;  // Another message start
        #10 in = 8'h8C;
        #10 in = 8'hF9;
        #10 in = 8'hCE;  // Another message start
        #10 in = 8'hC5;
        #10 in = 8'hAA;
        #10 in = 0;    // End stimulus
        #10 $finish;
    end

    // Monitor the tests
    initial begin
        $monitor("Time=%t, clk=%b, reset=%b, in=%h, done=%b, out_bytes=%h", 
                 $time, clk, reset, in, done, out_bytes);
    end

    // Check outputs and display result
    reg [23:0] expected_out_bytes;
    reg [2:0] byte_count;

    always @(posedge clk) begin
        if (reset) begin
            byte_count <= 3'b0;
        end else begin
            if (in[3] == 1'b1)  // byte starts after detection of leading 1 in bit 3
                byte_count <= 1;
            else if (byte_count > 0 && byte_count < 3)
                byte_count <= byte_count + 1;
            else
                byte_count <= 0;

            if (byte_count == 1) begin
                expected_out_bytes[23:16] <= in;
            end else if (byte_count == 2) begin
                expected_out_bytes[15:8] <= in;
            end else if (byte_count == 3) begin
                expected_out_bytes[7:0] <= in;
            end
        end

        // Check at done signal
        if (done) begin
            if (expected_out_bytes != out_bytes) begin
                $display("Test Case failed at %t: Expected %h, got %h", $time, expected_out_bytes, out_bytes);
                $finish;
            end
        end
    end

    // Finish simulation and check for success
    initial begin
        #200; // Wait for all operations
        $display("all test cases passed");
        $finish;
    end

endmodule

