module testbench;
    reg clk;
    reg reset;
    reg ena;
    wire pm;
    wire [7:0] hh;
    wire [7:0] mm;
    wire [7:0] ss;

    top_module DUT (
        .clk(clk),
        .reset(reset),
        .ena(ena),
        .pm(pm),
        .hh(hh),
        .mm(mm),
        .ss(ss)
    );

    // Clock generation
    initial begin
        clk = 0;
        forever #5 clk = ~clk;    // Fast clock (100 MHz assuming 10ns period)
    end

    // Test cases
    initial begin
        // Initialize signals
        reset = 1; ena = 0;
        #10;                     // Wait for reset to take effect
        reset = 0;

        // Check initial condition post reset (12:00 AM)
        // NOTICE: BCD for 12 -> 0001_0010 in hh
        if (hh !== 8'b0001_0010 || mm !== 8'b0 || ss !== 8'b0 || pm !== 0) begin
            $display("Test case reset failed.");
            $finish;
        end

        // Test case: Increment seconds
        ena = 1;
        repeat (59) begin
            #10; // Simulate 1 second
            if (ss == 8'b0101_1001 && mm !== 8'b0) begin // Last second of a minute
                $display("Test case seconds increment failed.");
                $finish;
            end
        end

        // Check rollover of seconds and increment of minutes
        #10;
        if (!(ss === 8'b0 && mm === 8'b0000_0001)) begin
            $display("Test case minute increment on seconds rollover failed.");
            $finish;
        end

        // Test case: Increment minutes until hour increments and check AM/PM toggle
        repeat (59) begin
            repeat (60) #10; // Complete minutes
        end

        #10;
        if (hh !== 8'b0001_0011 || pm !== 1) begin // Check for 1 PM
            $display("Test case hour increment or PM toggle failed.");
            $finish;
        end

        // Reset in between normal operation
        reset = 1; #10; reset = 0;
        if (hh !== 8'b0001_0010 || mm !== 8'b0 || ss !== 8'b0 || pm !== 0) begin
            $display("Test case mid-operation reset failed.");
            $finish;
        end

        // Check 12-hour full rotation
        ena = 1;
        repeat (12*60*60) #10; // Simulate 12 hours

        #10;
        if (hh !== 8'b0001_0010) begin // Should be 12:00 again, PM indicator toggled
            $display("Test case 12-hour rotation failed.");
            $finish;
        end

        if (pm !== 1) begin
            $display("PM indicator test fail on 12-hour rotation.");
            $finish;
        end

        // ALL tests passed
        $display("all test cases passed");
        $finish;
    end
endmodule

