module tb_top_module();

    // Inputs
    reg clk;
    reg reset;
    reg w;

    // Outputs
    wire z;

    // Instantiate the Unit Under Test (UUT)
    top_module uut(
        .clk(clk), 
        .reset(reset), 
        .w(w), 
        .z(z)
    );

    // Clock generation
    initial begin
        clk = 0;
        forever #5 clk = ~clk; // Generate a clock with 10ns period
    end

    // Test cases
    initial begin
        // Initial state
        reset = 1; #10; // Active high reset
        reset = 0;

        // Test sequence to pass through various states
        // A(0) -0-> A
        w = 0; #10;
        check(z, 0, "A to A");

        // A(0) -1-> B
        w = 1; #10;
        check(z, 0, "A to B");

        // B(0) -0-> D
        w = 0; #10;
        check(z, 0, "B to D");

        // D(0) -0-> A
        w = 0; #10;
        check(z, 0, "D to A");

        // B(0) -1-> C
        w = 1; #10;
        check(z, 0, "B to C");

        // C(0) -1-> E
        w = 1; #10;
        check(z, 0, "C to E");

        // E(1) -0-> D
        w = 0; #10;
        check(z, 1, "E to D");

        // D(0) -1-> F
        w = 1; #10;
        check(z, 0, "D to F");

        // F(1) -1-> C
        w = 1; #10;
        check(z, 1, "F to C");

        // E(1) -1-> E
        w = 1; #10;
        check(z, 1, "E to E");

        // Test reset
        reset = 1; #10; // Reset back to A
        check(z, 0, "Reset to A");
        reset = 0;

        // Final Pass Test Check
        if (passed) $display("all test cases passed");

        // Finish the simulation
        #10 $finish;
    end

    // Checker task
    integer total_tests = 0;
    integer tests_passed = 0;
    reg passed = 1;

    task check;
      input expect_z;
      input real_z;
      input [256*8:1] testname;
    begin
        total_tests = total_tests + 1;
        if (expect_z !== real_z) begin
            $display("Test %s failed: expected %d, got %d", testname, expect_z, real_z);
            passed = 0;
        end
        else begin
            tests_passed = tests_passed + 1;
            $display("Test %s passed", testname);
        end
    end
    endtask

endmodule

