    .clk(clk),
    .load(load),
    .ena(ena),
    .amount(amount),
    .data(data),
    .q(q)
  );

  // Clock generation
  initial begin
    clk = 0;
    forever #5 clk = ~clk;  // Clock period of 10ns
  end

  // Test Cases
  initial begin
    test_failed = 0;
    data = 64'hA5A5A5A5A5A5A5A5;
    load = 0;
    ena = 0;
    amount = 2'b00;

    // Test 1: Load data, shift should not occur
    #10;
    load = 1; ena = 1;
    expected_q = data;
    #10;
    load = 0;
    if (q !== expected_q) test_failed = 1;

    // Test 2: Shift left by 1 bit
    amount = 2'b00;
    expected_q = expected_q << 1;
    #10;
    if (q !== expected_q) test_failed = 1;

    // Test 3: Shift left by 8 bits
    amount = 2'b01;
    expected_q = expected_q << 8;
    #10;
    if (q !== expected_q) test_failed = 1;

    // Test 4: Shift right by 1 bit (arithmetic)
    amount = 2'b10;
    expected_q = $signed(expected_q) >>> 1;
    #10;
    if (q !== expected_q) test_failed = 1;

    // Test 5: Shift right by 8 bits (arithmetic)
    amount = 2'b11;
    expected_q = $signed(expected_q) >>> 8;
    #10;
    if (q !== expected_q) test_failed = 1;

    if (!test_failed) $display("all test cases passed");
    $finish;
  end

