        clk = 0;
        forever #5 clk = ~clk; // clock with period 10ns
    end

    initial begin
        pass_flag = 1'b1;

        // Reset the DUT
        reset = 1; #10;
        reset = 0;

        // Test sequence
        test_state(1'b1, 3'b000, 3'b000, 1'b0); // A -> (w=1) -> A
        test_state(1'b0, 3'b000, 3'b001, 1'b0); // A -> (w=0) -> B
        test_state(1'b1, 3'b001, 3'b011, 1'b0); // B -> (w=1) -> D
        test_state(1'b0, 3'b001, 3'b010, 1'b0); // B -> (w=0) -> C
        test_state(1'b1, 3'b010, 3'b011, 1'b0); // C -> (w=1) -> D
        test_state(1'b0, 3'b010, 3'b100, 1'b0); // C -> (w=0) -> E
        test_state(1'b1, 3'b011, 3'b000, 1'b0); // D -> (w=1) -> A
        test_state(1'b0, 3'b011, 3'b101, 1'b1); // D -> (w=0) -> F
