        // Verify all tests passed
        if (pass_flag) begin
            $display("all test cases passed");
        end

        $finish;
    end

    // Task to test state transitions
    task test_state;
        input w_in;
        input [2:0] expected_state;
        input [2:0] expected_next_state;
        input expected_output;
        begin
            w = w_in;
            #10;
