module top_module_tb;

    // Inputs
    reg clk;
    reg areset;
    reg train_valid;
    reg train_taken;

    // Outputs
    wire [1:0] state;

    // Instantiate the Unit Under Test (UUT)
    top_module uut (
        .clk(clk),
        .areset(areset),
        .train_valid(train_valid),
        .train_taken(train_taken),
        .state(state)
    );

    initial begin
        // Initialize Inputs
        clk = 0;
        areset = 0;
        train_valid = 0;
        train_taken = 0;

        // Wait 100 ns for global reset to finish
        #100;

        // Add stimulus here
        // Test Case 1: Reset test
        areset = 1; #10; areset = 0; #10;
        if (state !== 2'b01) $display("Reset test failed");

        // Test Case 2: Increment to max (saturating at 3)
        train_valid = 1; train_taken = 1;
        repeat (5) begin
            #10;
            clk = ~clk; #10; clk = ~clk;
        end
        if (state !== 2'b11) $display("Increment to max test failed");

        // Test Case 3: Decrement to min (saturating at 0)
        train_taken = 0;
        repeat (5) begin
            #10;
            clk = ~clk; #10; clk = ~clk;
        end
        if (state !== 2'b00) $display("Decrement to min test failed");

        // Test Case 4: Remain the same when not training
        train_valid = 0;
        #10;
        clk = ~clk; #10; clk = ~clk;
        if (state !== 2'b00) $display("Remain the same test failed");

        // Test Case 5: Increment test from 0 to 2
        train_valid = 1; train_taken = 1;
        #10;
        clk = ~clk; #10; clk = ~clk;   // Should be 01
        #10;
        clk = ~clk; #10; clk = ~clk;   // Should be 10
        if (state !== 2'b10) $display("Increment test from 0 to 2 failed");

        // Test Case 6: Decrement test from 2 to 1
        train_taken = 0;
        #10;
        clk = ~clk; #10; clk = ~clk;   // Should be 01
        if (state !== 2'b01) $display("Decrement test from 2 to 1 failed");

        // Check for "all test cases passed"
        $display("All test cases passed.");

        // Finish the simulation
        #100 $finish;
    end

    // Generate clock
    always #5 clk = ~clk;

endmodule

