module tb_top_module;

    // Inputs
    reg clk;
    reg areset;
    reg bump_left;
    reg bump_right;
    reg ground;

    // Outputs
    wire walk_left;
    wire walk_right;
    wire aaah;

    // Instantiate the Device Under Test (DUT)
    top_module dut (
        .clk(clk),
        .areset(areset),
        .bump_left(bump_left),
        .bump_right(bump_right),
        .ground(ground),
        .walk_left(walk_left),
        .walk_right(walk_right),
        .aaah(aaah)
    );

    // Clock generation
    initial begin
        clk = 0;
        forever #5 clk = ~clk;  // Clock period of 10ns
    end

    // Test cases
    initial begin
        // Initial reset
        areset = 1;
        #10 areset = 0;

        // Test 1: Check initial state is walking left
        if (walk_left !== 1 || walk_right !== 0 || aaah !== 0) begin
            $finish;
        end

        // Test 2: Bump right
        bump_right = 1;
        #10;
        bump_right = 0;
        if (walk_left !== 0 || walk_right !== 1 || aaah !== 0) begin
            $finish;
        end

        // Test 3: Ground disappears
        ground = 0;
        #10;
        if (walk_left !== 0 || walk_right !== 0 || aaah !== 1) begin
            $finish;
        end

        // Test 4: Ground reappears, should continue walking right
        ground = 1;
        #10;
        if (walk_left !== 0 || walk_right !== 1 || aaah !== 0) begin
            $finish;
        end

        // Test 5: Bump left
        bump_left = 1;
        #10;
        bump_left = 0;
        if (walk_left !== 1 || walk_right !== 0 || aaah !== 0) begin
            $finish;
        end

        // Test 6: Bump both sides
        bump_left = 1;
        bump_right = 1;
        #10;
        bump_left = 0;
        bump_right = 0;
        if (walk_left !== 0 || walk_right !== 1 || aaah !== 0) begin
            $finish;
        end

        // Test 7: Make sure falling does not change state
        ground = 0;
        #10;
        ground = 1;
        #10;
        if (walk_left !== 0 || walk_right !== 1 || aaah !== 0) begin
            $finish;
        end

        // Pass all test cases
        $display("all test cases passed");
        $finish;
    end

endmodule

