    #10; state = 10'b0000000001; in = 0; expected_state = 10'b0000000001; expected_out1 = 0; expected_out2 = 0; // S0 -0-> S0
    #10; in = 1; expected_state = 10'b0000000010; // S0 -1-> S1
    #10; in = 0; expected_state = 10'b0000000001; // S1 -0-> S0
