    #10; in = 0; expected_state = 10'b0000000001; // S4 -0-> S0
    #10; in = 1; expected_state = 10'b0000100000; // S4 -1-> S5
    #10; in = 0; expected_state = 10'b1000000000; // S5 -0-> S8
    #10; in = 1; expected_state = 10'b0001000000; // S5 -1-> S6
